{
    if (deviceData.payload.type == 'scalar')
    {
        sysSelectValue[deviceData.plotDevice].innerHTML = deviceData.payload.value;
        $('#unit_'+ deviceData.plotDevice.toString()).html(deviceData.payload.unit);

        var valueColor = 'white';
        if (deviceData.payload.alarm > 0) valueColor = 'yellow';
        if (deviceData.payload.alarm > 2) valueColor = 'red';
        $('#sysSelectValue_'+ deviceData.plotDevice.toString()).css("color", valueColor);

        if (plotStarted)
        {
          traces[deviceData.plotDevice].x.push((new Date().getTime() - plotStartTime) / 1000);
          traces[deviceData.plotDevice].y.push(deviceData.payload.value);
        }
    }
}
function selectAxis(plotDevice,axis)
{
  plotAxis[plotDevice] = axis;
}
function selectPts(plotDevice,axis)
{
  plotPts[plotDevice] = $("#pts_" + plotDevice.toString()).is(':checked');
}
function startPlot()
{
  if (!plotStarted)
  {
    $("#startPlotButton").html('Stop Plot');

  }
  else
  {
    $("#startPlotButton").html('Start Plot');
  }
  plotStarted = !plotStarted;
  if (!plotStarted)
  {
    activeTraces = [];
    return;
  }
  for (var idev = 0; idev < numPlotDevices; ++idev)
  {
    var modeType = 'lines';
    if (plotPts[idev]) modeType = 'markers';
    traces[idev] = {
      x: [],
      y: [],
      name: plotAxis[idev] + ' ' + getDevName(idev) + ' (' + $("#unit_" + idev.toString()).text() + ')',
      yaxis: plotAxis[idev],
      type: 'scatter',
      mode: modeType
    }
    if (validDevice[idev]) activeTraces.push(traces[idev]);
  }
  plotStartTime = new Date().getTime();
  Plotly.newPlot('timePlot', activeTraces, layout);
  plotLength = Number($("#plotLength").val());

}

