template:
var ws;
var retries = 0;
var userID = -1;
var sysSelect = [];
var sysName = ['sys01','sys02','sys03','sys04','device','attr','prop'];
var numPlotDevices = 1;
var validDevice = [];
var numSys = sysName.length;
var numDeviceRequested = 0;
var numDeviceReceived = 0;
var graph3d = null;
var itimeSlice = 0;
var timeMax = 32;
var dataArray = [];
var timeStamp = [];
var deltaT = 0;
var freeze = false;
var sliceViewActive = false;
var birdsEyeViewActive = false;
var topViewActive = false;
var birdsEyeTimeMax = 32;
var plotStarted = false;

var threedPlotOptions =
{
    width: '850px',
    height: '600px',
    style: 'surface',
    showPerspective: false,
    showGrid: true,
    showShadow: false,
    keepAspectRatio: false,
    verticalRatio: 0.6,
    showZAxis: true,
    xLabel: 'x',
    yLabel: 'Time',
    tooltip: true,
    axisColor: '#ffffff',
};

$( document ).ready(function()
{
    $("#freezeButton").text('Running');
    $("#plotslices").val(timeMax.toString());
    $("#plotslices").change(function()
    {
      timeMax = Number($("#plotslices").val());
      if (timeMax < 2)
      {
          alert('Number of plot slices must be greater than 1');
          timeMax = 2;
      }
      birdsEyeTimeMax = timeMax;
      $("#plotslices").val(timeMax.toString());
      dataArray = [];
      itimeSlice = 0;
      plotStarted = false;
    });
    sliceViewActive = true;
    birdsEyeViewActive = false;
    topViewActive = false;

    userID  = getRandomInt(4096);
    wsConnectC();
    for (var idev = 0; idev < numPlotDevices; ++idev)
    {
        sysSelect[idev] = [];
        for (var isys = 0; isys < numSys; ++isys)
        {
            sysSelect[idev][isys] = document.getElementById('sysSelect_' + idev.toString() + '_'+ isys.toString());
        }
        validDevice[idev] = false;
        setInterval(getDevVector,1000);
    }

});
function wsConnectC()
{
    var uri = window.location.href.split('://');
    var wslead = 'ws://';
    if (uri[0] == 'https') wslead = 'wss://';
    ws = new WebSocket(wslead + uri[1] + '/websocket');
    ws.onmessage = function(event)
    {
        var msg = JSON.parse(event.data);
        if (msg.userID == userID)
        {
            switch(msg.topic)
            {
                case 'loadSystem':
                    loadSystem(msg);
                    break;
                case 'putDevVector':
                    putDevVector(msg);
                    break;
                default:
                // code block
            }
        }

    };
    ws.onopen = function()
    {
        console.log("Websocket connected");
        for (var idev = 0; idev < numPlotDevices; ++idev)
        {
            ws.send(JSON.stringify(
            {
                topic       : 'getDevSystem',
                system      : sysName[0],
                payload     : {},
                'userID'    : userID,
                plotDevice      : idev,
                sysSelectIndex  : 0,
            }));
        }
    };
    ws.onclose = function()
    {
    };
}
function getRandomInt(max)
{
  return Math.floor(Math.random() * Math.floor(max));
}
function loadSystem(systemData)
{
    clearDownStreamColumns(systemData.plotDevice, systemData.sysSelectIndex);
    for (var ii = 0; ii < systemData.payload.length; ++ii)
    {
        opt = document.createElement('option');
        opt.value = systemData.payload[ii];
        opt.innerHTML = systemData.payload[ii];
        sysSelect[systemData.plotDevice][systemData.sysSelectIndex].appendChild(opt);
    }
}
function clearDownStreamColumns(plotDevice, sysSelectIndex)
{
    for (var isys = sysSelectIndex; isys < numSys; ++isys)
    {
        while (sysSelect[plotDevice][isys].firstChild)
        {
            sysSelect[plotDevice][isys].removeChild(sysSelect[plotDevice][isys].firstChild);
        }
    }
    var opt = document.createElement('option');
    opt.value = 'notSelected';
    opt.innerHTML = '';
    sysSelect[plotDevice][sysSelectIndex].appendChild(opt);
    validDevice[plotDevice] = false;
    plotStarted = false;
    sliceViewActive = true;
    birdsEyeViewActive = false;
    topViewActive = false;
}
function sysSelected(plotDevice, sysSelectIndex)
{
    if (sysSelect[plotDevice][sysSelectIndex].value == 'notSelected') return;
    if(sysSelect[plotDevice][sysSelectIndex].firstChild.value == 'notSelected')
    {
        sysSelect[plotDevice][sysSelectIndex].removeChild(sysSelect[plotDevice][sysSelectIndex].firstChild);
    }
    if (sysSelectIndex < (numSys - 1))
    {
        var newMsg =
            {
                topic           : 'getDevSystem',
                system          : sysName[sysSelectIndex + 1],
                payload         : {},
                userID          : userID,
                plotDevice      : plotDevice,
                sysSelectIndex  : sysSelectIndex + 1
            };
        for (var isys = 0; isys <= sysSelectIndex; ++isys)
        {
            newMsg.payload[sysName[isys]] = sysSelect[plotDevice][isys].value;
        }
        ws.send(JSON.stringify(newMsg));
    }
    else
    {
        validDevice[plotDevice] = true;
    }

}
function getDevName(plotDevice)
{
    if (!validDevice[plotDevice]) return;
    var devName = sysSelect[plotDevice][0].value;
    for (var isys = 1; isys < numSys; ++isys)
    {
        devName = devName + '-' + sysSelect[plotDevice][isys].value;
    }
    return devName;
}
function getDevVector()
{
    for (var idev = 0; idev < numPlotDevices; ++idev)
    {
        if (validDevice[idev])
        {
            var newMsg =
                {
                    topic           : 'getDevVector',
                    payload         : {},
                    userID          : userID,
                    plotDevice      : idev,
                };
            for (var isys = 0; isys < numSys; ++isys)
            {
                newMsg.payload[sysName[isys]] = sysSelect[idev][isys].value;
            }
            ws.send(JSON.stringify(newMsg));
        }
    }
}
function putDevVector(deviceData)
{
  var npts = deviceData.payload.value[0].length;
  if(!plotStarted)
  {
    dataArray = [];
    for (var ix = 0; ix < npts; ++ix)
    {
      dataArray[ix] =
      {
        'x': deviceData.payload.value[0][ix],
        'y': 0,
        'z': deviceData.payload.value[1][ix],
        'style': deviceData.payload.value[1][ix]
      };
    }
    var container = document.getElementById('spectrogram');

    graph3d = new vis.Graph3d(container, dataArray, threedPlotOptions);
    $("#startTime").text(new Date().toLocaleString());
    $("#stopTime").text(new Date().toLocaleString());
    graph3d.on('cameraPositionChange', onCameraPositionChange);
    if (sliceViewActive) sliceView();
    if (birdsEyeViewActive) birdsEyeView();
    if (topViewActive) topView();
    plotStarted = true;
  }
  else
  {
    if (freeze) return;
    if (itimeSlice < timeMax)
    {
      timeStamp[itimeSlice] = new Date().getTime();
      deltaT = (timeStamp[itimeSlice] - timeStamp[0]) / 1000;
      for (var ix = 0; ix < npts; ++ix)
      {
        dataArray[itimeSlice * npts + ix] =
        {
          'x': deviceData.payload.value[0][ix],
          'y': deltaT,
          'z': deviceData.payload.value[1][ix],
          'style': deviceData.payload.value[1][ix]
        };
      }
      ++itimeSlice;
    }
    else
    {
      for (var itime = 0; itime < (timeMax - 1); ++itime)
      {
        timeStamp[itime] = timeStamp[itime + 1];
        deltaT = (timeStamp[itime] - timeStamp[0]) / 1000;
        for (var ix = 0; ix < npts; ++ix)
        {
          dataArray[itime * npts + ix] =
          {
            'x': dataArray[(itime + 1) * npts + ix].x,
            'y': deltaT,
            'z': dataArray[(itime + 1) * npts + ix].z,
            'style': dataArray[(itime + 1) * npts + ix].style
          };
        }
      }
      timeStamp[timeMax - 1] = new Date().getTime();
      deltaT = (timeStamp[timeMax - 1] - timeStamp[0]) / 1000;
      for (var ix = 0; ix < npts; ++ix)
      {
        dataArray[(timeMax - 1) * npts + ix] =
        {
          'x': deviceData.payload.value[0][ix],
          'y': deltaT,
          'z': deviceData.payload.value[1][ix],
          'style': deviceData.payload.value[1][ix]
        };
      }
    }
    $("#startTime").text(new Date(timeStamp[0]).toLocaleString());
    $("#stopTime").text(new Date(timeStamp[itimeSlice - 1]).toLocaleString());
    graph3d.setData(dataArray);
  }
}
function freezeRun()
{
  freeze = !freeze;
  if (freeze)
  {
    $("#freezeButton").text('Frozen');
  }
  else
  {
    $("#freezeButton").text('Running');
  }

}

function birdsEyeView()
{
  var horzAngle = 315.0 * 3.1415927 / 180.0;
  var vertAngle = 45.0 * 3.1415927 / 180.0;
  graph3d.setCameraPosition({'horizontal': horzAngle, 'vertical': vertAngle, 'distance': 2.0});
  if (sliceViewActive)
  {
    timeMax =  birdsEyeTimeMax;
    $("#plotslices").val(timeMax.toString());
  }
    sliceViewActive = false;
    birdsEyeViewActive = true;
    topViewActive = false;

}
function sliceView()
{
  var horzAngle = 0.0 * 3.1415927 / 180.0;
  var vertAngle = 0.0 * 3.1415927 / 180.0;
  graph3d.setCameraPosition({'horizontal': horzAngle, 'vertical': vertAngle, 'distance': 1.1});
  timeMax = 2;
  $("#plotslices").val(timeMax.toString());
  itimeSlice = 0;
  plotStarted = false;
  sliceViewActive = true;
  birdsEyeViewActive = false;
  topViewActive = false;

}
function topView()
{
  var horzAngle = 0 * 3.1415927 / 180.0;
  var vertAngle = 90 * 3.1415927 / 180.0;
  graph3d.setCameraPosition({'horizontal': horzAngle, 'vertical': vertAngle, 'distance': 2.0});
  if (sliceViewActive)
  {
    timeMax =  birdsEyeTimeMax;
    $("#plotslices").val(timeMax.toString());
  }
    sliceViewActive = false;
    birdsEyeViewActive = false;
    topViewActive = true;

}
function onCameraPositionChange(event)
{
  if (sliceViewActive) sliceView();
  if (birdsEyeViewActive) birdsEyeView();
  if (topViewActive) topView();
}

