        typeCell.setAttribute("id", "alarmTable" + irow.toString() + "_type");
        $('#alarmTable' + irow.toString()).hide();
        iconCell.innerHTML = "<img id='alarmTable" + irow.toString() + "_led'" + " src='/img/greenLight.png' height='50px'/>";
    }

}
function alarmScan(deviceData)
{
    var deviceInfo = [];
    var nrows = $('#alarmTable tr').length;
    nrows = nrows - 1;
    if (deviceData.payload.length > nrows) alert('Too many alarms to display!');
    for (var idev = 0; idev < deviceData.payload.length; ++idev)
    {
        deviceInfo[idev] = deviceData.payload[idev].sys01;
        deviceInfo[idev] = deviceInfo[idev] + '-' + deviceData.payload[idev].sys02;
        deviceInfo[idev] = deviceInfo[idev] + '-' + deviceData.payload[idev].sys03;
        deviceInfo[idev] = deviceInfo[idev] + '-' + deviceData.payload[idev].sys04;
        deviceInfo[idev] = deviceInfo[idev] + '-' + deviceData.payload[idev].device;
        deviceInfo[idev] = deviceInfo[idev] + '-' + deviceData.payload[idev].attr;
        deviceInfo[idev] = deviceInfo[idev] + '-' + deviceData.payload[idev].prop;
        $('#alarmTable' + idev.toString()).show();
        $('#alarmTable' + idev.toString() + '_dev').html(deviceInfo[idev]);
        $('#alarmTable' + idev.toString() + '_val').html(deviceData.payload[idev].value.toString());
        $('#alarmTable' + idev.toString() + '_unit').html(deviceData.payload[idev].unit);
        var type = '';
        if (deviceData.payload[idev].alarm == 1) type = 'LOW';
        if (deviceData.payload[idev].alarm == 2) type = 'HIGH';
        if (deviceData.payload[idev].alarm == 3) type = 'LOLO';
        if (deviceData.payload[idev].alarm == 4) type = 'HIHI';
        $('#alarmTable' + idev.toString() + '_type').html(type);
        if (deviceData.payload[idev].alarm < 3)
        {
            $('#alarmTable' + idev.toString() + '_led').attr('src','/img/yellowLight.png');
        }
        else
        {
            $('#alarmTable' + idev.toString() + '_led').attr('src','/img/redLight.png');
            if (alarmSoundOn)
            {
                if ((new Date().getTime() - soundLastPlayed) > soundInterval)
                {
                    audioAlarm.play();
                    soundLastPlayed = new Date().getTime();
                }
            }

        }

    }
    for (var idev = deviceData.payload.length; idev < nrows; ++idev)
    {
        $('#alarmTable' + idev.toString()).hide();
    }

}
function toggleSound()
{
    alarmSoundOn = !alarmSoundOn;
    var soundButtonText = 'Turn Sound On';
    if (alarmSoundOn) soundButtonText = 'Turn Sound Off';
    $('#toggleSoundButton').html(soundButtonText);
}

